@app.route("/", methods = ["GET"])
def main():
    return "Hello u wanna got response go to /skill"

@app.route("/test", methods=["GET", "POST"])
def test():
    data = jsonify(
    version = 1.0,
    value_list =[
        "abc",
        "def"
    ])
    return data

